import {
  call,
  cancelled,
  put,
  take,
  takeEvery,
  takeLeading,
} from "redux-saga/effects";
import { END, eventChannel } from "redux-saga";
import createWebSocketConnection from "../webSocket/socket";
import {
  ADD_MSG,
  CONNECT_SOCKET,
  SOCKET_CONNECTION_ERROR,
  SOCKET_CONNECTION_SUCCESS,
} from "../modules/chat";

function createEventChannel(socket) {
  return eventChannel((emit) => {
    socket.onmessage = (event) => {
      emit(event.data);
    };
    socket.onclose = () => {
      emit(END);
    };
  });
}

function* listenForSocketMessages() {
  let socket;
  let socketChannel;

  try {
    socket = yield call(createWebSocketConnection);
    socketChannel = yield call(createEventChannel, socket);

    yield put(SOCKET_CONNECTION_SUCCESS);

    while (true) {
      const payload = yield take(socketChannel);
      yield put(ADD_MSG(payload));
    }
  } catch (e) {
    yield put(SOCKET_CONNECTION_ERROR);
  } finally {
    if (yield cancelled()) {
      socketChannel.close();
      socket.close();
    } else {
      yield put(SOCKET_CONNECTION_ERROR("WebSocket disconnected"));
    }
  }
}

export function* connectWebSocket() {
  yield takeEvery(CONNECT_SOCKET, listenForSocketMessages);

